module.exports = function(sequelize, DataTypes) {
    var Tenant = sequelize.define('CSDB_Tenant', {
            Name: {type: DataTypes.STRING, allowNull: false, unique: true},
            Description: DataTypes.STRING,
            Active:DataTypes.BOOLEAN



        }
    );

    return Tenant;
};
